package com.blogapplication.controller;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import com.blogapplication.exception.ResourceNotFoundException;import com.blogapplication.request.CategoryRequest;import com.blogapplication.response.CategoryResponse;import com.blogapplication.service.Impl.CategoryServiceImpl;import lombok.AllArgsConstructor;@RestController@AllArgsConstructor@RequestMapping("/api/blogApp/category")public class CategoryController {	@Autowired	private CategoryServiceImpl categoryService;	@PostMapping("/save")	public ResponseEntity<CategoryResponse> addCategory(@RequestBody CategoryRequest categoryRequest)			throws ResourceNotFoundException {		return new ResponseEntity<>(categoryService.addCategory(categoryRequest), HttpStatus.CREATED);	}	@GetMapping("/getAll")	public List<CategoryResponse> getAllCategory() {		return categoryService.getAllCategory();	}	@GetMapping("/getById/{id}")	public ResponseEntity<CategoryResponse> getCategoryById(@PathVariable("id") Integer categoryId)			throws ResourceNotFoundException {		return new ResponseEntity<>(categoryService.getCategoryById(categoryId), HttpStatus.OK);	}	@DeleteMapping("/softDelete/{id}")	public ResponseEntity<String> softDeleteCategory(@PathVariable("id") Integer categoryId)			throws ResourceNotFoundException {		return new ResponseEntity<>(categoryService.softDeleteCategory(categoryId), HttpStatus.OK);	}	@DeleteMapping("/{id}")	public ResponseEntity<String> deleteCategory(@PathVariable("id") Integer categoryId)			throws ResourceNotFoundException {		return new ResponseEntity<>(categoryService.deleteCategory(categoryId), HttpStatus.OK);	}}